    /**
     * Event listener for add preview button
     * Takes all necessary parameters and calls the method addOrderToPreview()
     * @see addOrderToPreview()
     * @param evt 
     */
    private void AddPreviewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddPreviewActionPerformed
        updateBoxSizes();
        int grade = CardGrade.getSelectedIndex()+1;
        int colour = (int) Colour.getSelectedIndex();
        boolean reinforcedBottom = ReinforcedBottom.isSelected();
        boolean reinforcedCorners = ReinforcedCorners.isSelected();
        boolean sealableTop = SealableTop.isSelected();
        int numOfBoxes = (int)Quantity.getValue();
        
        addOrderToPreview(boxLength, boxHeight, boxWidth,grade,colour,reinforcedBottom,reinforcedCorners,sealableTop,numOfBoxes);
    }//GEN-LAST:event_AddPreviewActionPerformed

